const bytewidth = msg.config.bitwidth/8
const input = msg.payload
const output = Buffer.alloc(input.length/2)

if (bytewidth == 1) {
    for (let i=0;i<output.length;i+=1) {
        output[i] = input[i*2];
    }
    msg.payload = output
} else if (bytewidth == 2) {
    for (let i=0;i<output.length;i+=2) {
        output[i] = input[i*2]
        output[i+1] = input[i*2+1]
    }
    msg.payload = output
} else if (bytewidth == 3) {
    for (let i=0;i<output.length;i+=3) {
        output[i] = input[i*2]
        output[i+1] = input[i*2+1]
        output[i+2] = input[i*2+2]
    }
    msg.payload = output
}

return msg;

